mapper.sql:
where 1=1


    and id = #{id}


    and activityId = #{activityId}


    and phone = #{phone}


    and status = #{status}


    and channel = #{channel}

mapper.update:
update t_activity_sign_up set activityPackageId = #{activityPackageId}, amount = #{amount}, 
	name = #{name}, phone = #{phone}, IDCard = #{IDCard}, remark = #{remark}, updateTime = #{updateTime}
where id = #{id}
